import React, { createContext, useContext, useReducer } from 'react';
import ReactDOM from 'react-dom/client';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import './index.css';

// Simple cart state management
const CartContext = createContext();

const cartReducer = (state, action) => {
  switch (action.type) {
    case 'ADD_TO_CART':
      const existingItem = state.items.find(item => item.id === action.payload.id);
      if (existingItem) {
        return {
          ...state,
          items: state.items.map(item =>
            item.id === action.payload.id
              ? { ...item, quantity: item.quantity + 1 }
              : item
          )
        };
      }
      return {
        ...state,
        items: [...state.items, { ...action.payload, quantity: 1 }]
      };
    case 'REMOVE_FROM_CART':
      return {
        ...state,
        items: state.items.filter(item => item.id !== action.payload)
      };
    case 'CLEAR_CART':
      return {
        ...state,
        items: []
      };
    default:
      return state;
  }
};

const CartProvider = ({ children }) => {
  const [cart, dispatch] = useReducer(cartReducer, { items: [] });
  
  const addToCart = (product) => {
    dispatch({ type: 'ADD_TO_CART', payload: product });
  };
  
  const removeFromCart = (id) => {
    dispatch({ type: 'REMOVE_FROM_CART', payload: id });
  };
  
  const clearCart = () => {
    dispatch({ type: 'CLEAR_CART' });
  };
  
  const getTotalItems = () => {
    return cart.items.reduce((total, item) => total + item.quantity, 0);
  };
  
  const getTotalPrice = () => {
    return cart.items.reduce((total, item) => total + (item.price * item.quantity), 0);
  };
  
  return (
    <CartContext.Provider value={{
      cart,
      addToCart,
      removeFromCart,
      clearCart,
      getTotalItems,
      getTotalPrice
    }}>
      {children}
    </CartContext.Provider>
  );
};

const useCart = () => {
  const context = useContext(CartContext);
  if (!context) {
    throw new Error('useCart must be used within CartProvider');
  }
  return context;
};

function App() {
  return (
    <CartProvider>
      <BrowserRouter>
        <div style={{ minHeight: '100vh', background: 'linear-gradient(135deg, #667eea 0%, #764ba2 50%, #f093fb 100%)' }}>
          <Header />
          <div style={{ maxWidth: '1200px', margin: '0 auto', padding: '20px', backgroundColor: 'rgba(255,255,255,0.9)', borderRadius: '15px', boxShadow: '0 8px 32px rgba(0,0,0,0.1)' }}>
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/cart" element={<Cart />} />
            </Routes>
          </div>
        </div>
      </BrowserRouter>
    </CartProvider>
  );
}

function Header() {
  const { getTotalItems } = useCart();
  
  return (
    <header style={{ 
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)', 
      color: 'white', 
      padding: '1rem 0', 
      marginBottom: '2rem',
      boxShadow: '0 4px 15px rgba(0,0,0,0.2)'
    }}>
      <nav style={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center',
        maxWidth: '1200px',
        margin: '0 auto',
        padding: '0 20px'
      }}>
        <Link to="/" style={{ 
          color: 'white', 
          textDecoration: 'none', 
          fontSize: '3rem', 
          fontWeight: 'bold',
          background: 'linear-gradient(45deg, #ff6b6b, #4ecdc4, #45b7d1, #96ceb4)',
          WebkitBackgroundClip: 'text',
          WebkitTextFillColor: 'transparent',
          backgroundClip: 'text'
        }}>
          🛍️ BRAND NU U
        </Link>
        <Link to="/cart">
          <button style={{
            background: 'linear-gradient(45deg, #ff6b6b, #ee5a24)',
            color: 'white',
            border: 'none',
            padding: '10px 20px',
            borderRadius: '25px',
            cursor: 'pointer',
            boxShadow: '0 4px 15px rgba(255,107,107,0.3)',
            transition: 'transform 0.2s',
            fontSize: '1rem',
            fontWeight: 'bold'
          }}>
            🛒 Cart ({getTotalItems()})
          </button>
        </Link>
      </nav>
    </header>
  );
}

function Home() {
  const { addToCart } = useCart();
  
  const sampleProducts = [
    { 
      id: 1, 
      title: "Premium T-Shirt", 
      price: 19.99, 
      description: "High quality cotton t-shirt with modern design",
      category: "clothing",
      sizes: ["XS", "S", "M", "L", "XL", "XXL"],
      image: "https://images.unsplash.com/photo-1521572163474-6864f9cf17ab?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80"
    },
    { 
      id: 2, 
      title: "Running Sneakers", 
      price: 79.99, 
      description: "Comfortable running shoes for daily exercise",
      category: "footwear",
      sizes: {
        men: ["7", "7.5", "8", "8.5", "9", "9.5", "10", "10.5", "11", "11.5", "12"],
        women: ["5", "5.5", "6", "6.5", "7", "7.5", "8", "8.5", "9", "9.5", "10"]
      },
      image: "https://images.unsplash.com/photo-1542291026-7eec264c27ff?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80"
    },
    { 
      id: 3, 
      title: "Smart Watch", 
      price: 199.99, 
      description: "Track your fitness goals with this smart watch",
      category: "electronics",
      image: "https://images.unsplash.com/photo-1523275335684-37898b6baf30?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80"
    },
    { 
      id: 4, 
      title: "Wireless Headphones", 
      price: 99.99, 
      description: "High-quality wireless headphones with noise cancellation",
      category: "electronics",
      image: "https://images.unsplash.com/photo-1505740420928-5e560c06d30e?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80"
    },
    { 
      id: 5, 
      title: "Backpack", 
      price: 49.99, 
      description: "Durable backpack perfect for travel and daily use",
      category: "accessories",
      image: "https://images.unsplash.com/photo-1553062407-98eeb64c6a62?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80"
    },
    { 
      id: 6, 
      title: "Premium Coffee Mug", 
      price: 12.99, 
      description: "Beautiful ceramic coffee mug with elegant design and comfortable handle",
      category: "home",
      image: "https://images.unsplash.com/photo-1739018778115-d6d2504f5c45?q=80&w=1977&auto=format&fit=crop&ixlib=rb-4.1.0&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D"
    }
  ];
  
  return (
    <div>
      <h1 style={{ 
        marginBottom: '0.5rem', 
        color: '#4a5568',
        textAlign: 'left',
        fontSize: '2.5rem',
        fontWeight: 'bold'
      }}>
        WELCOME TO BRAND NU U
      </h1>
      <p style={{ 
        marginBottom: '1rem', 
        color: '#666',
        textAlign: 'left',
        fontSize: '1.2rem',
        fontWeight: 'normal'
      }}>
        Browse our collection and add items to your cart.
      </p>
      <h2 style={{ 
        color: '#333',
        fontSize: '1.5rem',
        textAlign: 'center',
        marginBottom: '2rem',
        fontWeight: 'bold'
      }}>
        Product Catalog
      </h2>
      
      <div style={{ 
        display: 'grid', 
        gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))', 
        gap: '20px'
      }}>
        {sampleProducts.map((product, index) => {
          const cardColors = [
            'linear-gradient(135deg, #ff9a9e 0%, #fecfef 100%)',
            'linear-gradient(135deg, #a8edea 0%, #fed6e3 100%)',
            'linear-gradient(135deg, #ffecd2 0%, #fcb69f 100%)',
            'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
            'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)',
            'linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)'
          ];
          
          return (
            <div key={product.id} style={{ 
              background: cardColors[index % cardColors.length],
              borderRadius: '20px',
              padding: '1.5rem',
              boxShadow: '0 8px 25px rgba(0,0,0,0.15)',
              transition: 'transform 0.3s, box-shadow 0.3s',
              cursor: 'pointer'
            }}
            onMouseEnter={(e) => {
              e.target.style.transform = 'translateY(-10px)';
              e.target.style.boxShadow = '0 15px 35px rgba(0,0,0,0.2)';
            }}
            onMouseLeave={(e) => {
              e.target.style.transform = 'translateY(0)';
              e.target.style.boxShadow = '0 8px 25px rgba(0,0,0,0.15)';
            }}>
              <img
                src={product.image}
                alt={product.title}
                style={{
                  width: '100%',
                  height: '200px',
                  objectFit: 'cover',
                  marginBottom: '1rem',
                  borderRadius: '15px',
                  border: '3px solid rgba(255,255,255,0.5)'
                }}
              />
              <h3 style={{ 
                fontSize: '1.2rem', 
                marginBottom: '0.5rem', 
                color: 'white',
                textShadow: '2px 2px 4px rgba(0,0,0,0.3)',
                fontWeight: 'bold'
              }}>
                {product.title}
              </h3>
              <p style={{ 
                color: 'white', 
                fontWeight: 'bold', 
                fontSize: '1.3rem', 
                marginBottom: '0.5rem',
                textShadow: '1px 1px 2px rgba(0,0,0,0.3)'
              }}>
                ${product.price.toFixed(2)}
              </p>
              <div style={{
                backgroundColor: 'rgba(255,255,255,0.8)',
                padding: '0.3rem 0.8rem',
                borderRadius: '20px',
                fontSize: '0.9rem',
                marginBottom: '0.5rem',
                display: 'inline-block',
                color: '#333',
                fontWeight: 'bold'
              }}>
                {product.category}
              </div>
              <p style={{
                color: 'rgba(255,255,255,0.9)',
                fontSize: '0.9rem',
                marginBottom: '1rem',
                lineHeight: 1.4,
                textShadow: '1px 1px 2px rgba(0,0,0,0.3)'
              }}>
                {product.description}
              </p>
              <button
                onClick={() => addToCart(product)}
                style={{
                  background: 'linear-gradient(45deg, #28a745, #20c997)',
                  color: 'white',
                  border: 'none',
                  padding: '12px 24px',
                  borderRadius: '25px',
                  cursor: 'pointer',
                  width: '100%',
                  fontSize: '1rem',
                  fontWeight: 'bold',
                  boxShadow: '0 4px 15px rgba(40,167,69,0.3)',
                  transition: 'transform 0.2s'
                }}
                onMouseEnter={(e) => {
                  e.target.style.transform = 'scale(1.05)';
                }}
                onMouseLeave={(e) => {
                  e.target.style.transform = 'scale(1)';
                }}
              >
                ✨ Add to Cart ✨
              </button>
            </div>
          );
        })}
      </div>
    </div>
  );
}

function Cart() {
  const { cart, removeFromCart, clearCart, getTotalPrice } = useCart();
  
  if (cart.items.length === 0) {
    return (
      <div style={{ textAlign: 'center', padding: '2rem' }}>
        <h1 style={{ 
          background: 'linear-gradient(45deg, #ff6b6b, #4ecdc4)', 
          WebkitBackgroundClip: 'text', 
          WebkitTextFillColor: 'transparent',
          backgroundClip: 'text',
          fontSize: '2.5rem'
        }}>
          Shopping Cart
        </h1>
        <p style={{ fontSize: '1.1rem', color: '#666', marginBottom: '2rem' }}>
          Your cart is empty. Start shopping to add items!
        </p>
        <Link to="/">
          <button style={{
            background: 'linear-gradient(45deg, #667eea, #764ba2)',
            color: 'white',
            border: 'none',
            padding: '15px 30px',
            borderRadius: '25px',
            cursor: 'pointer',
            fontSize: '1rem',
            fontWeight: 'bold',
            boxShadow: '0 4px 15px rgba(102,126,234,0.3)',
            transition: 'transform 0.2s'
          }}
          onMouseEnter={(e) => {
            e.target.style.transform = 'scale(1.05)';
          }}
          onMouseLeave={(e) => {
            e.target.style.transform = 'scale(1)';
          }}>
            🛍️ Continue Shopping
          </button>
        </Link>
      </div>
    );
  }
  
  return (
    <div>
      <h1 style={{ 
        background: 'linear-gradient(45deg, #ff6b6b, #4ecdc4)', 
        WebkitBackgroundClip: 'text', 
        WebkitTextFillColor: 'transparent',
        backgroundClip: 'text',
        fontSize: '2.5rem',
        textAlign: 'center',
        marginBottom: '2rem'
      }}>
        Shopping Cart
      </h1>
      <div style={{ marginBottom: '20px' }}>
        {cart.items.map((item, index) => {
          const cardColors = [
            'linear-gradient(135deg, #ff9a9e 0%, #fecfef 100%)',
            'linear-gradient(135deg, #a8edea 0%, #fed6e3 100%)',
            'linear-gradient(135deg, #ffecd2 0%, #fcb69f 100%)',
            'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
            'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)',
            'linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)'
          ];
          
          return (
            <div key={item.id} style={{
              background: cardColors[index % cardColors.length],
              borderRadius: '20px',
              padding: '1.5rem',
              marginBottom: '1rem',
              display: 'flex',
              alignItems: 'center',
              gap: '1rem',
              boxShadow: '0 8px 25px rgba(0,0,0,0.15)'
            }}>
              <img
                src={item.image}
                alt={item.title}
                style={{ 
                  width: '100px', 
                  height: '100px', 
                  objectFit: 'cover', 
                  borderRadius: '15px',
                  border: '3px solid rgba(255,255,255,0.5)'
                }}
              />
              <div style={{ flex: 1 }}>
                <h3 style={{ 
                  marginBottom: '0.5rem', 
                  color: 'white',
                  textShadow: '2px 2px 4px rgba(0,0,0,0.3)',
                  fontSize: '1.2rem'
                }}>
                  {item.title}
                </h3>
                <p style={{ 
                  color: 'white', 
                  fontWeight: 'bold',
                  textShadow: '1px 1px 2px rgba(0,0,0,0.3)'
                }}>
                  ${item.price.toFixed(2)} each
                </p>
                <p style={{ color: 'rgba(255,255,255,0.9)' }}>Quantity: {item.quantity}</p>
                <p style={{ color: 'white', fontWeight: 'bold', fontSize: '1.1rem' }}>
                  Subtotal: ${(item.price * item.quantity).toFixed(2)}
                </p>
              </div>
              <button 
                onClick={() => removeFromCart(item.id)}
                style={{
                  background: 'linear-gradient(45deg, #dc3545, #c82333)',
                  color: 'white',
                  border: 'none',
                  padding: '10px 20px',
                  borderRadius: '15px',
                  cursor: 'pointer',
                  fontWeight: 'bold',
                  boxShadow: '0 4px 15px rgba(220,53,69,0.3)'
                }}
              >
                🗑️ Remove
              </button>
            </div>
          );
        })}
      </div>
      
      <div style={{ 
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)', 
        borderRadius: '20px',
        padding: '2rem',
        boxShadow: '0 8px 25px rgba(0,0,0,0.15)',
        color: 'white'
      }}>
        <h2 style={{ textShadow: '2px 2px 4px rgba(0,0,0,0.3)' }}>Cart Summary</h2>
        <div style={{ marginBottom: '15px', fontSize: '1.1rem' }}>
          <strong>Total Items: {cart.items.reduce((total, item) => total + item.quantity, 0)}</strong>
        </div>
        <div style={{ marginBottom: '25px', fontSize: '1.5rem' }}>
          <strong>Total Price: ${getTotalPrice().toFixed(2)}</strong>
        </div>
        <button 
          onClick={() => {
            clearCart();
            alert('🎉 Checkout successful! Thank you for shopping with BRAND NU U!');
          }}
          style={{
            background: 'linear-gradient(45deg, #28a745, #20c997)',
            color: 'white',
            border: 'none',
            padding: '15px 30px',
            borderRadius: '25px',
            cursor: 'pointer',
            marginRight: '15px',
            fontSize: '1.1rem',
            fontWeight: 'bold',
            boxShadow: '0 4px 15px rgba(40,167,69,0.3)'
          }}
        >
          ✨ Checkout ✨
        </button>
        <Link to="/">
          <button style={{
            background: 'linear-gradient(45deg, #6c757d, #5a6268)',
            color: 'white',
            border: 'none',
            padding: '15px 30px',
            borderRadius: '25px',
            cursor: 'pointer',
            fontSize: '1.1rem',
            fontWeight: 'bold',
            boxShadow: '0 4px 15px rgba(108,117,125,0.3)'
          }}>
            🛍️ Continue Shopping
          </button>
        </Link>
      </div>
    </div>
  );
}

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(<App />);
