import React, { createContext, useContext, useReducer, useState } from 'react';
import ReactDOM from 'react-dom/client';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import './index.css';

// Simple cart state management
const CartContext = createContext();

const cartReducer = (state, action) => {
  switch (action.type) {
    case 'ADD_TO_CART':
      const existingItem = state.items.find(item => 
        item.id === action.payload.id && 
        item.selectedSize === action.payload.selectedSize &&
        item.selectedGender === action.payload.selectedGender
      );
      if (existingItem) {
        return {
          ...state,
          items: state.items.map(item =>
            item.id === action.payload.id && 
            item.selectedSize === action.payload.selectedSize &&
            item.selectedGender === action.payload.selectedGender
              ? { ...item, quantity: item.quantity + 1 }
              : item
          )
        };
      }
      return {
        ...state,
        items: [...state.items, { ...action.payload, quantity: 1 }]
      };
    case 'REMOVE_FROM_CART':
      return {
        ...state,
        items: state.items.filter(item => 
          !(item.id === action.payload.id && 
            item.selectedSize === action.payload.selectedSize &&
            item.selectedGender === action.payload.selectedGender)
        )
      };
    case 'CLEAR_CART':
      return {
        ...state,
        items: []
      };
    default:
      return state;
  }
};

const CartProvider = ({ children }) => {
  const [cart, dispatch] = useReducer(cartReducer, { items: [] });
  
  const addToCart = (product) => {
    dispatch({ type: 'ADD_TO_CART', payload: product });
  };
  
  const removeFromCart = (item) => {
    dispatch({ type: 'REMOVE_FROM_CART', payload: item });
  };
  
  const clearCart = () => {
    dispatch({ type: 'CLEAR_CART' });
  };
  
  const getTotalItems = () => {
    return cart.items.reduce((total, item) => total + item.quantity, 0);
  };
  
  const getTotalPrice = () => {
    return cart.items.reduce((total, item) => total + (item.price * item.quantity), 0);
  };
  
  return (
    <CartContext.Provider value={{
      cart,
      addToCart,
      removeFromCart,
      clearCart,
      getTotalItems,
      getTotalPrice
    }}>
      {children}
    </CartContext.Provider>
  );
};

const useCart = () => {
  const context = useContext(CartContext);
  if (!context) {
    throw new Error('useCart must be used within CartProvider');
  }
  return context;
};

function App() {
  return (
    <CartProvider>
      <BrowserRouter>
        <div style={{ minHeight: '100vh', background: 'linear-gradient(135deg, #667eea 0%, #764ba2 50%, #f093fb 100%)' }}>
          <Header />
          <div style={{ maxWidth: '1200px', margin: '0 auto', padding: '20px', backgroundColor: 'rgba(255,255,255,0.9)', borderRadius: '15px', boxShadow: '0 8px 32px rgba(0,0,0,0.1)' }}>
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/cart" element={<Cart />} />
            </Routes>
          </div>
        </div>
      </BrowserRouter>
    </CartProvider>
  );
}

function Header() {
  const { getTotalItems } = useCart();
  
  return (
    <header style={{ 
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)', 
      color: 'white', 
      padding: '1rem 0', 
      marginBottom: '2rem',
      boxShadow: '0 4px 15px rgba(0,0,0,0.2)'
    }}>
      <nav style={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center',
        maxWidth: '1200px',
        margin: '0 auto',
        padding: '0 20px'
      }}>
        <Link to="/" style={{ 
          color: 'white', 
          textDecoration: 'none', 
          fontSize: '3rem', 
          fontWeight: 'bold',
          background: 'linear-gradient(45deg, #ff6b6b, #4ecdc4, #45b7d1, #96ceb4)',
          WebkitBackgroundClip: 'text',
          WebkitTextFillColor: 'transparent',
          backgroundClip: 'text'
        }}>
          üõçÔ∏è BRAND NU U
        </Link>
        <Link to="/cart">
          <button style={{
            background: 'linear-gradient(45deg, #ff6b6b, #ee5a24)',
            color: 'white',
            border: 'none',
            padding: '10px 20px',
            borderRadius: '25px',
            cursor: 'pointer',
            boxShadow: '0 4px 15px rgba(255,107,107,0.3)',
            transition: 'transform 0.2s',
            fontSize: '1rem',
            fontWeight: 'bold'
          }}>
            üõí Cart ({getTotalItems()})
          </button>
        </Link>
      </nav>
    </header>
  );
}

function ProductCard({ product, index, addToCart }) {
  const [selectedSize, setSelectedSize] = useState('');
  const [selectedGender, setSelectedGender] = useState('men');
  
  const cardColors = [
    'linear-gradient(135deg, #ff9a9e 0%, #fecfef 100%)',
    'linear-gradient(135deg, #a8edea 0%, #fed6e3 100%)',
    'linear-gradient(135deg, #ffecd2 0%, #fcb69f 100%)',
    'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
    'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)',
    'linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)'
  ];

  const handleAddToCart = () => {
    if (product.sizes && !selectedSize) {
      alert('Please select a size before adding to cart!');
      return;
    }
    
    const productWithSize = {
      ...product,
      selectedSize: selectedSize || undefined,
      selectedGender: product.sizes && typeof product.sizes === 'object' ? selectedGender : undefined
    };
    
    addToCart(productWithSize);
  };

  return (
    <div key={product.id} style={{ 
      background: cardColors[index % cardColors.length],
      borderRadius: '20px',
      padding: '1.5rem',
      boxShadow: '0 8px 25px rgba(0,0,0,0.15)',
      transition: 'transform 0.3s, box-shadow 0.3s',
      cursor: 'pointer'
    }}
    onMouseEnter={(e) => {
      e.target.style.transform = 'translateY(-10px)';
      e.target.style.boxShadow = '0 15px 35px rgba(0,0,0,0.2)';
    }}
    onMouseLeave={(e) => {
      e.target.style.transform = 'translateY(0)';
      e.target.style.boxShadow = '0 8px 25px rgba(0,0,0,0.15)';
    }}>
      <img
        src={product.image}
        alt={product.title}
        style={{
          width: '100%',
          height: '200px',
          objectFit: 'cover',
          marginBottom: '1rem',
          borderRadius: '15px',
          border: '3px solid rgba(255,255,255,0.5)'
        }}
      />
      <h3 style={{ 
        fontSize: '1.2rem', 
        marginBottom: '0.5rem', 
        color: 'white',
        textShadow: '2px 2px 4px rgba(0,0,0,0.3)',
        fontWeight: 'bold'
      }}>
        {product.title}
      </h3>
      <p style={{ 
        color: 'white', 
        fontWeight: 'bold', 
        fontSize: '1.3rem', 
        marginBottom: '0.5rem',
        textShadow: '1px 1px 2px rgba(0,0,0,0.3)'
      }}>
        ${product.price.toFixed(2)}
      </p>
      <div style={{
        backgroundColor: 'rgba(255,255,255,0.8)',
        padding: '0.3rem 0.8rem',
        borderRadius: '20px',
        fontSize: '0.9rem',
        marginBottom: '0.5rem',
        display: 'inline-block',
        color: '#333',
        fontWeight: 'bold'
      }}>
        {product.category}
      </div>
      
      {/* Size Selection */}
      {product.sizes && (
        <div style={{ marginBottom: '0.5rem' }}>
          {Array.isArray(product.sizes) ? (
            // T-shirt sizes dropdown
            <div style={{ marginBottom: '0.5rem' }}>
              <label style={{ 
                color: 'white', 
                fontWeight: 'bold', 
                fontSize: '0.9rem',
                display: 'block',
                marginBottom: '0.3rem',
                textShadow: '1px 1px 2px rgba(0,0,0,0.3)'
              }}>
                Select Size:
              </label>
              <select 
                value={selectedSize} 
                onChange={(e) => setSelectedSize(e.target.value)}
                style={{
                  width: '100%',
                  padding: '0.5rem',
                  borderRadius: '10px',
                  border: 'none',
                  fontSize: '0.9rem',
                  fontWeight: 'bold',
                  backgroundColor: 'rgba(255,255,255,0.95)',
                  color: '#333',
                  cursor: 'pointer'
                }}
              >
                <option value="">Choose size...</option>
                {product.sizes.map(size => (
                  <option key={size} value={size}>{size}</option>
                ))}
              </select>
            </div>
          ) : (
            // Shoe sizes with gender selection
            <div>
              <label style={{ 
                color: 'white', 
                fontWeight: 'bold', 
                fontSize: '0.9rem',
                display: 'block',
                marginBottom: '0.3rem',
                textShadow: '1px 1px 2px rgba(0,0,0,0.3)'
              }}>
                Select Gender:
              </label>
              <select 
                value={selectedGender} 
                onChange={(e) => {
                  setSelectedGender(e.target.value);
                  setSelectedSize(''); // Reset size when gender changes
                }}
                style={{
                  width: '100%',
                  padding: '0.5rem',
                  borderRadius: '10px',
                  border: 'none',
                  fontSize: '0.9rem',
                  fontWeight: 'bold',
                  backgroundColor: 'rgba(255,255,255,0.95)',
                  color: '#333',
                  cursor: 'pointer',
                  marginBottom: '0.5rem'
                }}
              >
                <option value="men">Men's</option>
                <option value="women">Women's</option>
              </select>
              
              <label style={{ 
                color: 'white', 
                fontWeight: 'bold', 
                fontSize: '0.9rem',
                display: 'block',
                marginBottom: '0.3rem',
                textShadow: '1px 1px 2px rgba(0,0,0,0.3)'
              }}>
                Select Size:
              </label>
              <select 
                value={selectedSize} 
                onChange={(e) => setSelectedSize(e.target.value)}
                style={{
                  width: '100%',
                  padding: '0.5rem',
                  borderRadius: '10px',
                  border: 'none',
                  fontSize: '0.9rem',
                  fontWeight: 'bold',
                  backgroundColor: 'rgba(255,255,255,0.95)',
                  color: '#333',
                  cursor: 'pointer'
                }}
              >
                <option value="">Choose size...</option>
                {product.sizes[selectedGender].map(size => (
                  <option key={size} value={size}>{size}</option>
                ))}
              </select>
            </div>
          )}
        </div>
      )}
      
      <p style={{
        color: 'rgba(255,255,255,0.9)',
        fontSize: '0.9rem',
        marginBottom: '1rem',
        lineHeight: 1.4,
        textShadow: '1px 1px 2px rgba(0,0,0,0.3)'
      }}>
        {product.description}
      </p>
      <button
        onClick={handleAddToCart}
        style={{
          background: 'linear-gradient(45deg, #28a745, #20c997)',
          color: 'white',
          border: 'none',
          padding: '12px 24px',
          borderRadius: '25px',
          cursor: 'pointer',
          width: '100%',
          fontSize: '1rem',
          fontWeight: 'bold',
          boxShadow: '0 4px 15px rgba(40,167,69,0.3)',
          transition: 'transform 0.2s'
        }}
        onMouseEnter={(e) => {
          e.target.style.transform = 'scale(1.05)';
        }}
        onMouseLeave={(e) => {
          e.target.style.transform = 'scale(1)';
        }}
      >
        ‚ú® Add to Cart ‚ú®
      </button>
    </div>
  );
}

