              {category === 'all' ? 'üåü All Products' : 
               category === 'electronics' ? 'üì± Electronics' :
               category === 'jewelery' ? 'üíé Jewelry' :
               category === "men's clothing" ? 'üëî Men\'s Clothing' :
               category === "women's clothing" ? 'üëó Women\'s Clothing' : 
               category}
            </button>
          ))}
        </div>
        
        {/* Results Count */}
        <div style={{
          fontSize: '14px',
          color: '#666',
          fontWeight: '500'
        }}>
          Showing {filteredProducts.length} of {sampleProducts.length} products
        </div>
      </div>
      
      <div style={{ 
        display: 'grid', 
        gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))', 
        gap: '15px'
      }}>
        {filteredProducts.map((product, index) => (
          <ProductCard 
            key={product.id}
            product={product}
            index={index}
            addToCart={addToCart}
          />
        ))}
      </div>
    </div>
  );
}

function Cart() {
  const { cart, removeFromCart, clearCart, getTotalPrice } = useCart();
  
  if (cart.items.length === 0) {
    return (
      <div style={{ textAlign: 'center', padding: '2rem' }}>
        <h1 style={{ 
          background: 'linear-gradient(45deg, #ff6b6b, #4ecdc4)', 
          WebkitBackgroundClip: 'text', 
          WebkitTextFillColor: 'transparent',
          backgroundClip: 'text',
          fontSize: '2.5rem'
        }}>
          Shopping Cart
        </h1>
        <p style={{ fontSize: '1.1rem', color: '#666', marginBottom: '2rem' }}>
          Your cart is empty. Start shopping to add items!
        </p>
        <Link to="/">
          <button style={{
            background: 'linear-gradient(45deg, #667eea, #764ba2)',
            color: 'white',
            border: 'none',
            padding: '15px 30px',
            borderRadius: '25px',
            cursor: 'pointer',
            fontSize: '1rem',
            fontWeight: 'bold',
            boxShadow: '0 4px 15px rgba(102,126,234,0.3)',
            transition: 'transform 0.2s'
          }}
          onMouseEnter={(e) => {
            e.target.style.transform = 'scale(1.05)';
          }}
          onMouseLeave={(e) => {
            e.target.style.transform = 'scale(1)';
          }}>
            üõçÔ∏è Continue Shopping
          </button>
        </Link>
      </div>
    );
  }
  
  return (
    <div>
      <h1 style={{ 
        background: 'linear-gradient(45deg, #ff6b6b, #4ecdc4)', 
        WebkitBackgroundClip: 'text', 
        WebkitTextFillColor: 'transparent',
        backgroundClip: 'text',
        fontSize: '2.5rem',
        textAlign: 'center',
        marginBottom: '2rem'
      }}>
        Shopping Cart
      </h1>
      <div style={{ marginBottom: '20px' }}>
        {cart.items.map((item, index) => {
          const cardColors = [
            'linear-gradient(135deg, #ff9a9e 0%, #fecfef 100%)',
            'linear-gradient(135deg, #a8edea 0%, #fed6e3 100%)',
            'linear-gradient(135deg, #ffecd2 0%, #fcb69f 100%)',
            'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
            'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)',
            'linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)'
          ];
          
          return (
            <div key={item.id} style={{
              background: cardColors[index % cardColors.length],
              borderRadius: '20px',
              padding: '1.5rem',
              marginBottom: '1rem',
              display: 'flex',
              alignItems: 'center',
              gap: '1rem',
              boxShadow: '0 8px 25px rgba(0,0,0,0.15)'
            }}>
              <img
                src={item.image}
                alt={item.title}
                style={{ 
                  width: '100px', 
                  height: '100px', 
                  objectFit: 'cover', 
                  borderRadius: '15px',
                  border: '3px solid rgba(255,255,255,0.5)'
                }}
              />
              <div style={{ flex: 1 }}>
                <h3 style={{ 
                  marginBottom: '0.5rem', 
                  color: 'white',
                  textShadow: '2px 2px 4px rgba(0,0,0,0.3)',
                  fontSize: '1.2rem'
                }}>
                  {item.title}
                </h3>
                {item.selectedSize && (
                  <div style={{
                    backgroundColor: 'rgba(255,255,255,0.9)',
                    padding: '0.2rem 0.6rem',
                    borderRadius: '12px',
                    fontSize: '0.8rem',
                    color: '#333',
                    fontWeight: 'bold',
                    display: 'inline-block',
                    marginBottom: '0.5rem'
                  }}>
                    {item.selectedGender ? `${item.selectedGender === 'men' ? 'Men\'s' : 'Women\'s'} Size: ${item.selectedSize}` : `Size: ${item.selectedSize}`}
                  </div>
                )}
                <p style={{ 
                  color: 'white', 
                  fontWeight: 'bold',
                  textShadow: '1px 1px 2px rgba(0,0,0,0.3)'
                }}>
                  ${item.price.toFixed(2)} each
                </p>
                <p style={{ color: 'rgba(255,255,255,0.9)' }}>Quantity: {item.quantity}</p>
                <p style={{ color: 'white', fontWeight: 'bold', fontSize: '1.1rem' }}>
                  Subtotal: ${(item.price * item.quantity).toFixed(2)}
                </p>
              </div>
              <button 
                onClick={() => removeFromCart(item)}
                style={{
                  background: 'linear-gradient(45deg, #dc3545, #c82333)',
                  color: 'white',
                  border: 'none',
                  padding: '10px 20px',
                  borderRadius: '15px',
                  cursor: 'pointer',
                  fontWeight: 'bold',
                  boxShadow: '0 4px 15px rgba(220,53,69,0.3)'
                }}
              >
                üóëÔ∏è Remove
              </button>
            </div>
          );
        })}
      </div>
      
      <div style={{ 
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)', 
        borderRadius: '20px',
        padding: '2rem',
        boxShadow: '0 8px 25px rgba(0,0,0,0.15)',
        color: 'white'
      }}>
        <h2 style={{ textShadow: '2px 2px 4px rgba(0,0,0,0.3)' }}>Cart Summary</h2>
        <div style={{ marginBottom: '15px', fontSize: '1.1rem' }}>
          <strong>Total Items: {cart.items.reduce((total, item) => total + item.quantity, 0)}</strong>
        </div>
        <div style={{ marginBottom: '25px', fontSize: '1.5rem' }}>
          <strong>Total Price: ${getTotalPrice().toFixed(2)}</strong>
        </div>
        <button 
          onClick={() => {
            clearCart();
            alert('üéâ Checkout successful! Thank you for shopping with BRAND NU U!');
          }}
          style={{
            background: 'linear-gradient(45deg, #28a745, #20c997)',
            color: 'white',
            border: 'none',
            padding: '15px 30px',
            borderRadius: '25px',
            cursor: 'pointer',
            marginRight: '15px',
            fontSize: '1.1rem',
            fontWeight: 'bold',
            boxShadow: '0 4px 15px rgba(40,167,69,0.3)'
          }}
        >
          ‚ú® Checkout ‚ú®
        </button>
        <Link to="/">
          <button style={{
            background: 'linear-gradient(45deg, #6c757d, #5a6268)',
            color: 'white',
            border: 'none',
            padding: '15px 30px',
            borderRadius: '25px',
            cursor: 'pointer',
            fontSize: '1.1rem',
            fontWeight: 'bold',
            boxShadow: '0 4px 15px rgba(108,117,125,0.3)'
          }}>
            üõçÔ∏è Continue Shopping
          </button>
        </Link>
      </div>
    </div>
  );
}

