import React from 'react';
import ReactDOM from 'react-dom/client';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import './index.css';

function App() {
  return (
    <BrowserRouter>
      <div style={{ padding: '20px' }}>
        <header style={{ 
          backgroundColor: '#333', 
          color: 'white', 
          padding: '1rem 0', 
          marginBottom: '2rem' 
        }}>
          <nav style={{ 
            display: 'flex', 
            justifyContent: 'space-between', 
            alignItems: 'center',
            maxWidth: '1200px',
            margin: '0 auto',
            padding: '0 20px'
          }}>
            <Link to="/" style={{ 
              color: 'white', 
              textDecoration: 'none', 
              fontSize: '1.5rem', 
              fontWeight: 'bold' 
            }}>
              üõçÔ∏è React E-Store
            </Link>
            <Link to="/cart">
              <button style={{
                backgroundColor: '#007bff',
                color: 'white',
                border: 'none',
                padding: '10px 20px',
                borderRadius: '5px',
                cursor: 'pointer'
              }}>
                üõí Cart (0)
              </button>
            </Link>
          </nav>
        </header>
        
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/cart" element={<Cart />} />
        </Routes>
      </div>
    </BrowserRouter>
  );
}

function Home() {
  return (
    <div>
      <h1>Product Catalog</h1>
      <div style={{ 
        display: 'grid', 
        gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))', 
        gap: '20px',
        marginTop: '20px'
      }}>
        <div style={{ 
          border: '1px solid #ddd', 
          padding: '15px', 
          borderRadius: '8px',
          backgroundColor: 'white',
          boxShadow: '0 2px 5px rgba(0,0,0,0.1)'
        }}>
          <h3>Sample Product</h3>
          <p style={{ color: '#007bff', fontWeight: 'bold' }}>$19.99</p>
          <p style={{ color: '#666', fontSize: '0.9rem' }}>Product description goes here...</p>
          <button style={{
            backgroundColor: '#28a745',
            color: 'white',
            border: 'none',
            padding: '8px 16px',
            borderRadius: '4px',
            cursor: 'pointer',
            width: '100%'
          }}>
            Add to Cart
          </button>
        </div>
      </div>
    </div>
  );
}

function Cart() {
  return (
    <div>
      <h1>Shopping Cart</h1>
      <p>Your cart is empty.</p>
      <Link to="/">
        <button style={{
          backgroundColor: '#007bff',
          color: 'white',
          border: 'none',
          padding: '10px 20px',
          borderRadius: '5px',
          cursor: 'pointer'
        }}>
          Continue Shopping
        </button>
      </Link>
    </div>
  );
}

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(<App />);
